let buttons=document.querySelectorAll(".services-item"),slider=document.querySelectorAll(".opredelenie");for(let t=0;t<buttons.length;t++){let l=buttons[t];l.addEventListener("click",function(){let e=document.querySelector(".current-button");e.classList.remove("current-button"),l.classList.add("current-button");for(let e=0;e<slider.length;e++)slider[e].classList.contains("visually-hidden-all")||slider[e].classList.add("visually-hidden-all");slider[t].classList.remove("visually-hidden-all")})}